import React from "react";
import {BrowserRouter} from "react-router-dom";
import {Button, Input, Select} from "antd";
import axios from "axios";
import {HOST, PORT} from "../config/apiconfig";

class Article extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            allCate: [], // 存放所有的类别
            selectedValue: "", // 记录当前选择器是文章标题还是文章类别
        }
    }

    getAllCate = () => { // 获取所有的类别
        axios.get(`${HOST}:${PORT}/article/cate`)
            .then(result => {
                if (result.data.status === 200) {
                    this.setState({
                        allCate: result.data.data
                    })
                }
            }).catch(err => {
            console.log(err);
        })
    }

    componentDidMount() { // 页面加载完成后执行
        this.getAllCate(); // 获取所有的类别
    }

    render() {
        const {allCate} = this.state;
        return (
            <BrowserRouter>
                <div style={{padding: "30px 20px"}}>
                    <div style={{width: "40%"}}>
                        <Input.Group compact style={{display: "flex"}} className={"select"}>
                            <Select defaultValue={"title"} onChange={value => { // 当变化时调用
                                console.log(value)
                                this.setState({
                                    selectedValue: value
                                })
                                // setState(!state); // 切换搜索途径时刷新一下
                            }}>
                                <Select.Option value={"title"}>文章标题</Select.Option>
                                <Select.Option value={"cate"}>文章类别</Select.Option>
                            </Select>
                            {allCate.map(item => {

                            })}
                            {/*{selectedValue == "cate" ? (*/}
                            {/*    <Select style={{width: "200px"}} defaultValue={0} onChange={value => { // value是0，1，2*/}
                            {/*        // console.log(value, selectedRole);*/}
                            {/*        // setSelectedRole(value);*/}
                            {/*        // console.log(selectedRole);*/}
                            {/*    }}>*/}
                            {/*        <Select.Option value={0}>超级管理员</Select.Option>*/}
                            {/*        <Select.Option value={1}>普通管理员</Select.Option>*/}
                            {/*        <Select.Option value={2}>账户已停用</Select.Option>*/}
                            {/*    </Select>*/}
                            {/*) : (<Input style={{width: "200px"}} id={"input"}/>)}&nbsp;&nbsp;&nbsp;*/}
                            <Button type={"primary"}>搜索</Button>&nbsp;&nbsp;&nbsp;
                        </Input.Group>
                    </div>
                </div>
            </BrowserRouter>
        )
    }
}

export default Article;
