import React from 'react';
import {Input, Button, Form, Row, Col, message} from "antd";
import {UserOutlined, LockOutlined} from "@ant-design/icons";
import {HOST, PORT} from "../config/apiconfig";
import "../css/login.css";
import axios from "axios";
import {BrowserRouter, NavLink} from "react-router-dom";
import logo from '../logo.png';
import $ from "jquery";

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: " ",
            password: " ",
            isLogin: false
        }
    }

    loginFormRef = React.createRef(); // 与render函数中的登录表单进行绑定

    componentDidMount() {
        if (this.state.isLogin == false) {
            $("body").css("backgroundColor", "#f2f4f7");
        } else {
            $("body").css("backgroundColor", "white");
        }
    }

    // componentWillUnmount() {
    //     this.setState({
    //         isLogin: false
    //     })
    // }

    login = async () => {
        // 对表单控件进行规则验证，验证设置了rules属性的控件（Form.Item）
        await this.loginFormRef.current.validateFields()
            .then(value => { // 验证成功，value是验证成功的控件的值
                this.setState({ // 设置当前状态为最新输入的值
                    username: value.username,
                    password: value.password
                })
                axios.post(`${HOST}:${PORT}/admins/login`, {user: value})
                    .then(result => {
                        if (result.data.status == 200) { // 登录成功
                            // 将用户名写入到sessionStorage中
                            sessionStorage.setItem("username", value.username);
                            // 将token写入到sessionStorage中
                            sessionStorage.setItem("token", result.data.token);
                            // 进行页面跳转
                            this.props.history.push("/home");
                            this.setState({
                                isLogin: true
                            })
                        } else {
                            message.error(result.data.msg);
                            return;
                        }
                    }).catch(err => {
                    console.log(err);
                })
            })
    }

    render() {
        return (
            <BrowserRouter>
                <section className="login-content">
                    <div>
                        <img src={logo} style={{width: "400px"}} alt="logo"/>
                    </div>
                    <div style={{marginLeft: "20px"}}>
                        <h2 className={"login-title"}>创客装修后台管理系统</h2>
                        <Form ref={this.loginFormRef}>
                            <Row>
                                <Col span={24}>
                                    <Form.Item
                                        name={"username"}
                                        rules={[  // 规则
                                            {
                                                required: true,
                                                message: '用户名不能为空'
                                            }
                                        ]}
                                    >
                                        <Input
                                            style={{borderRadius: "24px"}}
                                            prefix={<UserOutlined style={{color: 'rgba(0,0,0,0.25)'}}/>}
                                            placeholder={"请输入用户名"}
                                            size={"large"}/>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row>
                                <Col span={24}>
                                    <Form.Item name="password"
                                               rules={[
                                                   {
                                                       required: true,
                                                       message: '密码不能为空'
                                                   }

                                               ]}>
                                        <Input.Password
                                            style={{borderRadius: "24px"}}
                                            prefix={<LockOutlined style={{color: "rgba(0,0,0,0.25)"}}/>}
                                            placeholder={"请输入密码"}
                                            size={"large"}/>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row>
                                <Col span={24}>
                                    <Button type={"primary"} style={{width: "100%", borderRadius: "24px"}}
                                            size={"large"} onClick={this.login}>登录</Button>
                                </Col>
                            </Row>
                        </Form>
                        <div style={{float: "right", marginTop: "5px"}}>
                            <NavLink to={"/find"}>忘记密码？</NavLink>
                        </div>
                    </div>
                </section>
            </BrowserRouter>
        )
    }

}

export default Login;
