import React, {useEffect, useState} from "react";
import {BrowserRouter} from "react-router-dom";
import {Button, Form, Input, message, Modal, Radio, Select, Space, Table, Tag} from "antd";
import axios from "axios";
import {HOST, PORT} from "../config/apiconfig";
import {DeleteOutlined, EditOutlined, ExclamationCircleOutlined} from "@ant-design/icons";
import {Option} from "antd/es/mentions";


export const Administrator = () => {
    const [admins, setAdmins] = useState([]); // 存放所有的管理员数据
    const [state, setState] = useState(false); // 记录当前页面是否修改
    const [person, setPerson] = useState({}); // 记录要修改的用户的值
    const [modifyVisiable, setModifyVisiable] = useState(false); // 是否显示修改信息的模态框
    const [addVisiable, setAddVisiable] = useState(false); // 是否显示添加的模态框

    const modifyRef = React.createRef(); // 与修改模态框绑定
    const addRef = React.createRef(); // 与添加模态框绑定

    const {confirm} = Modal;

    // 打开模态框
    const showModifyModal = (record) => {
        setPerson(record);
        setModifyVisiable(true);
        // console.log(person);
    }
    const showAddModal = () => {
        setAddVisiable(true);
    }

    const addButton = [<Button type={"primary"} onClick={showAddModal}>添加</Button>] // 控制添加按钮显示

    // 关闭模态框
    const handleCancel = () => {
        if (modifyVisiable) {
            setModifyVisiable(false);
        }
        if (addVisiable) {
            setAddVisiable(false);
        }
    }

    // 查询所有的管理员
    const allAdministrator = () => {
        axios.get(`${HOST}:${PORT}/admins/all`)
            .then(result => {
                if (result.data.status === 200) {
                    setAdmins(result.data.data);
                }
                if (result.data.status == 400) {
                    message.error(result.data.msg);
                }
            })
    }

    // 定义管理员身份标签
    const tagArr = [
        <Tag color={"red"}>超级管理员</Tag>,
        <Tag color={"green"}>普通管理员</Tag>,
        <Tag color={"default"}>账号已停用</Tag>
    ]

    const columns = [ // 定义表格的列
        {
            title: "编号",
            dataIndex: "id",
            key: "id"
        },
        {
            title: "用户名",
            dataIndex: "username",
            key: "username",
            editable: true
        },
        {
            title: "姓名",
            dataIndex: "name",
            key: "name",
            editable: true
        },
        {
            title: "身份",
            dataIndex: "role",
            key: "role",
            render: role => (
                <div key={"role"}>
                    {tagArr[role]}
                </div>
            ),
            editable: true
        },
        {
            title: "上次登录",
            dataIndex: "last_login_at",
            key: "last_login_at"
        },
        {
            title: "创建时间",
            dataIndex: "created_at",
            key: "created_at"
        },
        {
            title: "更新时间",
            dataIndex: "updated_at",
            key: "updated_at"
        },
        sessionStorage.getItem("role") == 0 ?
            {
                title: "操作",
                key: "action",
                render: (text, record, index) => {// text：当前行的值，record：当前行的数据，index：当前行索引
                    // text和record是一样的结果，显示表格的具体信息,index是索引，该索引是表格的索引0,1,2
                    return (
                        <Space size={"middle"}>
                            <Button type={"link"} icon={<EditOutlined/>}
                                    onClick={() => showModifyModal(record)}>编辑</Button>
                            <Button type={"link"} icon={<DeleteOutlined/>} danger>删除</Button>
                        </Space>
                    )
                }
            } : {},
    ];

    const modify = async () => {
        await modifyRef.current.validateFields()
            .then(value => { // 修改后的数据
                axios.post(`${HOST}:${PORT}/admins/modifyInfo`, {user: value, id: person.id})
                    .then(result => {
                        if (result.data.status === 201) {
                            message.success(result.data.msg);
                            setModifyVisiable(false);
                            setState(!state);
                        }
                    }).catch(err => {
                    console.log(err);
                })
            })
    }


    const showModifyConfirm = () => {
        confirm({
            title: "确认修改",
            icon: <ExclamationCircleOutlined/>,
            onOk() {
                modify();
            },
            okText: "确定",
            cancelText: "取消",
            centered: true
        })
    }

    useEffect(() => allAdministrator(), [state]); // 设置刷新当前页面的条件

    return (
        <BrowserRouter>
            <div style={{padding: "30px 20px"}}>
                <div style={{width: "40%"}}>
                    <Input.Group compact style={{display: "flex"}}>
                        <Select defaultValue={"文章标题"}>
                            <Select.Option value={"title"}>文章标题</Select.Option>
                            <Select.Option value={"cate"}>文章类别</Select.Option>
                        </Select>
                        <Input style={{width: "200px"}}/>&nbsp;&nbsp;&nbsp;
                        <Button type={"primary"}>搜索</Button>&nbsp;&nbsp;&nbsp;
                        {addButton[sessionStorage.getItem("role")]}
                    </Input.Group>
                </div>
                <Table rowKey={record => record.id} columns={columns} dataSource={admins}
                       style={{marginTop: "20px"}}></Table>
                <Radio.Group/>
            </div>
            <Modal title={"修改管理员信息"} visible={modifyVisiable} okText={"修改"} cancelText={"取消"} onCancel={handleCancel}
                   destroyOnClose onOk={() => showModifyConfirm()} centered={true}>
                <Form ref={modifyRef}>
                    <Form.Item label="用&nbsp;&nbsp;户&nbsp;&nbsp;名" name={"username"} initialValue={person.username}
                               rules={[
                                   {
                                       required: true,
                                       message: "用户名不能为空"
                                   }
                               ]}>
                        <Input/>
                    </Form.Item>
                    <Form.Item label={"真实姓名"} name={"name"} initialValue={person.name} rules={[
                        {
                            required: true,
                            message: "真实姓名不能为空"
                        }
                    ]}>
                        <Input/>
                    </Form.Item>
                    <Form.Item label="身&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;份" name={"role"} rules={[
                        {
                            required: true
                        }
                    ]} initialValue={person.role}>
                        <Select>
                            <Select.Option value={0}>超级管理员</Select.Option>
                            <Select.Option value={1}>普通管理员</Select.Option>
                            <Select.Option value={2}>账户已停用</Select.Option>
                        </Select>
                    </Form.Item>
                </Form>
            </Modal>
        </BrowserRouter>
    )
}
