import React from "react";
import {Layout, Menu, Dropdown, Button} from "antd";
import {BrowserRouter, NavLink, Route, Switch} from "react-router-dom";
import {
    AppstoreFilled,
    BarsOutlined,
    CaretDownOutlined, DownOutlined, FileTextOutlined,
    GiftOutlined,
    HomeOutlined, PictureOutlined, SettingOutlined, TagsOutlined, TeamOutlined
} from "@ant-design/icons";
import OrderTable from "./OrderTable";
import Article from "./article";

const {Header, Content, Sider} = Layout;

class Frame extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            key: 1 // 记录点击到了哪个页面
        }
        this.menuRef = React.createRef();
    }

    // 个人信息下拉框
    DownMenu = () => {
        return (
            <Menu>
                <Menu.Item key={"1"}>
                    <a>个人主页</a>
                </Menu.Item>
                <Menu.Item key={"2"}>
                    <a>退出登录</a>
                </Menu.Item>
            </Menu>
        )
    }

    // 标签选项
    TagSelectMenu = () => {
        return (
            // 编写onClick事件
            <Menu>
                <Menu.Item key={"1"}>清空标签</Menu.Item>
                <Menu.Item key={"2"}>2</Menu.Item>
            </Menu>
        )
    }

    // 每个页面上的标志
    pageTitle = (key) => {
        switch (key) {
            case 1:
                return (
                    <div>
                        <HomeOutlined/>&nbsp;&nbsp;系统首页
                    </div>
                )
        }
    }

    render() {
        return (
            // BrowserRouter表示使用了history模式的路由，必须放在最外层
            <BrowserRouter>
                {/*使用顶部——侧边栏——通栏的布局*/}
                <div className={"container"}>
                    <Header className={"header"}>
                        <AppstoreFilled style={{color: "white", fontSize: "30px", marginTop: "17px", float: "left"}}/>
                        <div style={{
                            color: "white",
                            fontFamily: "微软雅黑",
                            fontSize: "25px",
                            float: "left",
                            marginLeft: "30px"
                        }}>创客装修后台管理系统
                        </div>
                        <Dropdown overlay={this.DownMenu} trigger={["click"]}>
                            <a className={"ant-dropdown-link"} onClick={e => e.preventDefault()}
                               style={{color: "white", float: "right"}}>
                                {sessionStorage.getItem("username")}<CaretDownOutlined/>
                            </a>
                        </Dropdown>
                    </Header>
                    <Layout>
                        <Sider width={200} className={"site-layout-background"} style={{backgroundColor: "white"}}>
                            <Menu mode={"inline"} defaultSelectedKeys={["1"]} defaultOpenKeys={["1"]}
                                  style={{height: "100%", borderRight: 0}}
                                  onClick={key => {
                                      console.log(key.key)
                                      this.setState({
                                          key: key.key
                                      })
                                      let key1 = this.state.key
                                      console.log(key1);
                                  }}
                                  itemRef={this.menuRef}>
                                <Menu.Item key={"1"} style={{marginTop: 0}}>
                                    <NavLink to={"/home"}>
                                        <HomeOutlined/>&nbsp;&nbsp;系统首页
                                    </NavLink>
                                </Menu.Item>
                                <Menu.Item key={"2"}>
                                    <NavLink to={"/orders"}>
                                        <BarsOutlined/>&nbsp;&nbsp;预约管理
                                    </NavLink>
                                </Menu.Item>
                                <Menu.Item key={"3"}>
                                    <NavLink to={"/active"}>
                                        <GiftOutlined/>&nbsp;&nbsp;活动管理
                                    </NavLink>
                                </Menu.Item>
                                <Menu.Item key={"4"}>
                                    <NavLink to={"/cate"}>
                                        <TagsOutlined/>&nbsp;&nbsp;分类管理
                                    </NavLink>
                                </Menu.Item>
                                <Menu.Item key={"5"}>
                                    <NavLink to={"/article"}>
                                        <FileTextOutlined/>&nbsp;&nbsp;文章管理
                                    </NavLink>
                                </Menu.Item>
                                <Menu.Item key={"6"}>
                                    <NavLink to={"/case"}>
                                        <PictureOutlined/>&nbsp;&nbsp;案例管理
                                    </NavLink>
                                </Menu.Item>
                                <Menu.Item key={"7"}>
                                    <NavLink to={"/info"}>
                                        <SettingOutlined/>&nbsp;&nbsp;企业信息管理
                                    </NavLink>
                                </Menu.Item>
                                <Menu.Item key={"8"}>
                                    <NavLink to={"/administrator"}>
                                        <TeamOutlined/>&nbsp;&nbsp;管理员管理
                                    </NavLink>
                                </Menu.Item>
                            </Menu>
                        </Sider>
                        <Layout style={{float: "right", marginLeft: "1px"}}>
                            <div style={{
                                width: "100%",
                                height: "40px",
                                backgroundColor: "white",
                                float: "right"
                            }}>
                                <div style={{float: "left"}}>点击一个页面添加一个标签</div>
                                <div style={{
                                    width: "130px",
                                    height: "100%",
                                    float: "right",
                                    borderLeft: "2px solid rgb(240,240,240)",
                                    marginTop: "4px"
                                }}>
                                    <Dropdown overlay={this.TagSelectMenu}>
                                        <Button type={"primary"} style={{
                                            float: "right",
                                            marginRight: "10px"
                                        }}>标签选项<DownOutlined/></Button>
                                    </Dropdown>
                                </div>
                            </div>
                            <Layout style={{
                                padding: "20px 24px 24px",
                                backgroundColor: "red",
                                minHeight: "400px"
                            }}>
                                {/*<PageTitle/>*/}
                                {
                                    this.pageTitle(this.menuRef.current.key)
                                }
                                {/*导航栏旁边的区域*/}
                                <Content className={"site-layout-background"}
                                         style={{
                                             minHeight: 300,
                                             marginTop: "30px",
                                             backgroundColor: "white"
                                         }}>
                                    <Switch>
                                        <Route path={"/home"}></Route>
                                        <Route path={"/orders"} component={OrderTable}></Route>
                                        <Route path={"/active"}></Route>
                                        <Route path={"/case"}></Route>
                                        <Route path={"/article"} component={Article}></Route>
                                        <Route path={"/info"}></Route>
                                        <Route path={"/cate"}></Route>
                                        <Route path={"/administrator"}></Route>
                                    </Switch>
                                </Content>
                            </Layout>
                        </Layout>
                    </Layout>
                </div>
            </BrowserRouter>
        );
    }
}

export default Frame;
