import React, {useEffect, useState} from "react";
import {BrowserRouter} from "react-router-dom";
import axios from "axios";
import {HOST, PORT} from "../config/apiconfig";
import {message} from "antd";

const Home = (props) => {
    const [person, setPerson] = useState({}); // 存放当前页面的数据
    const [state, setState] = useState(true);

    // let username = sessionStorage.getItem("username"); // 获取用户名
    // axios.get(`${HOST}:${PORT}/admins/one`, {params: {username: username}})
    //     .then(result => {
    //         if (result.data.status === 200) { // 成功查找到人
    //             setPerson(result.data.data);
    //         }
    //         if (result.status === 400) {
    //             message.error(result.data.msg);
    //         }
    //     }).catch(err => {
    //     console.log(err);
    // })

    const getPerson = () => {
        let username = sessionStorage.getItem("username"); // 获取用户名
        console.log(username);
        axios.get(`${HOST}:${PORT}/admins/one`, {params: {username: username}})
            .then(result => {
                if (result.data.status === 200) { // 成功查找到人
                    setPerson(result.data.data);
                }
                if (result.status === 400) {
                    message.error(result.data.msg);
                }
            }).catch(err => {
            console.log(err);
        })
    }
    // 每次都调用函数获取信息，当person更改时才调用
    useEffect(() => getPerson(), [state]);


    return (
        <BrowserRouter>
            <div style={{margin: "30px 20px"}}>
                {/*<p style={{fontSize: "30px", backgroundColor: "red"}}>{person.username}</p>*/}
                {/*<p style={{fontSize: "16px", backgroundColor: "pink", marginTop: "-30px"}}>{person.role}</p>*/}
                <hr/>
                <div>
                    上次登录：
                </div>
            </div>
        </BrowserRouter>
    )
}

export default Home;
