import React, {useState} from "react";
import {BrowserRouter} from "react-router-dom";
import {Button, Input, Card, PageHeader, Modal, Form} from "antd";
import BraftEditor from "braft-editor"; // 引入富文本编辑器
import "braft-editor/dist/index.css";
import {ExclamationCircleOutlined, VerticalAlignTopOutlined} from "@ant-design/icons"; // 引入css样式

const Case = function (props) {
    const [isAdd, setIsAdd] = useState(false); // 记录是否是添加页面
    const [editorState, setEditorState] = useState(BraftEditor.createEditorState("")); // 设置初始值

    const addCaseRef = React.createRef();

    const back = () => { // 退出编辑页面
        Modal.confirm({
            title: "确认退出",
            icon: <ExclamationCircleOutlined/>,
            content: "您的文章将不会被保存",
            okText: "确认",
            cancelText: "取消",
            onOk: () => setIsAdd(false),
        })
    }

    const submit = async () => { // 发布文章
        await addCaseRef.current.validateFields()
            .then(value => {
                console.log(value.braft.toHTML());
            })
    }

    const casePage = () => { // 显示案例
        return (
            <div>
                <Input.Group compact>
                    <Input style={{width: "200px"}} placeholder={"请输入案例名称"}/>&nbsp;&nbsp;&nbsp;
                    <Button type={"primary"}>搜索</Button>&nbsp;&nbsp;&nbsp;
                    <Button type={"primary"} onClick={() => {
                        setIsAdd(true);
                    }}>添加</Button>
                </Input.Group>
                {/*<Table  style={{marginTop: "20px"}}></Table>*/}
                {/*<div style={{marginTop: "20px"}}>*/}
                {/*    {*/}
                {/*        <Card hoverable>*/}

                {/*        </Card>*/}
                {/*    }*/}
                {/*</div>*/}
            </div>
        )
    }

    const addPage = () => { // 添加案例
        return (
            <div style={{position: "relative"}}>
                <PageHeader className={"site-page-header"} onBack={() => back()} title={"编辑案例"} extra={[
                    <Button key={1} type={"primary"} onClick={() => back()}>取消编辑</Button>,
                    <Button key={2} type={"primary"} onClick={() => submit()}>发布文章</Button>
                ]}>
                    <Form ref={addCaseRef}>
                        <Form.Item name={"title"} defaultValue={"【无标题】"}>
                            <Input placeholder={"请输入文章标题"}
                                   style={{borderRadius: "24px", height: "40px", padding: "8px"}}/>
                        </Form.Item>
                        <Form.Item name={"braft"}>
                            <BraftEditor placeholder={"在这里输入正文"} onChange={(content) => setEditorState(content)}/>
                        </Form.Item>
                    </Form>
                </PageHeader>
            </div>
        )
    }

    return (
        <BrowserRouter>
            <div style={{padding: "30px 20px"}}>
                {isAdd ? addPage() : casePage()}
            </div>
        </BrowserRouter>
    )
}

export default Case;
