import React, {useState} from "react";
import {BrowserRouter} from "react-router-dom";
import {Button, Input, Card, Table, PageHeader, Modal, Anchor, BackTop} from "antd";
import BraftEditor from "braft-editor"; // 引入富文本编辑器
import "braft-editor/dist/index.css";
import {ExclamationCircleOutlined} from "@ant-design/icons"; // 引入css样式

const Case = function (props) {
    const [isAdd, setIsAdd] = useState(false); // 记录是否是添加页面

    const back = () => {
        Modal.confirm({
            title: "确认退出",
            icon: <ExclamationCircleOutlined/>,
            content: "您的文章将不会被保存",
            okText: "确认",
            cancelText: "取消",
            onOk: () => setIsAdd(false),
        })
    }

    const casePage = () => { // 显示案例
        return (
            <div>
                <Input.Group compact>
                    <Input style={{width: "200px"}} placeholder={"请输入案例名称"}/>&nbsp;&nbsp;&nbsp;
                    <Button type={"primary"}>搜索</Button>&nbsp;&nbsp;&nbsp;
                    <Button type={"primary"} onClick={() => {
                        setIsAdd(true);
                    }}>添加</Button>
                </Input.Group>
                {/*<Table  style={{marginTop: "20px"}}></Table>*/}
                {/*<div style={{marginTop: "20px"}}>*/}
                {/*    {*/}
                {/*        <Card hoverable>*/}

                {/*        </Card>*/}
                {/*    }*/}
                {/*</div>*/}
            </div>
        )
    }

    const addPage = () => { // 添加案例
        return (
            <div style={{position: "relative"}}>
                <PageHeader className={"site-page-header"} onBack={() => back()} title={"编辑案例"} extra={[
                    <Button>提交</Button>
                ]}>
                    <Input key={"title"} id={"caseTitle"}/>
                    <BraftEditor onChange={(content) => console.log(content)}/>
                </PageHeader>
                {/*<div style={{position: "fixed", right: "80px", zIndex: 999, bottom: "80px"}}>回到顶部</div>*/}
            </div>
        )
    }

    return (
        <BrowserRouter>
            <div style={{padding: "30px 20px"}}>
                {isAdd ? addPage() : casePage()}
            </div>
        </BrowserRouter>
    )
}

export default Case;
