import React, {useState} from "react";
import "../css/login.css";
import {Button, Col, Form, Input, message, Row, Steps} from "antd";
import {LeftOutlined, LockOutlined, UserOutlined} from "@ant-design/icons";
import {NavLink} from "react-router-dom";
import axios from "axios";
import {HOST, PORT} from "../config/apiconfig";

const {Step} = Steps;

const Findpwd = () => {
    const form1Ref = React.createRef();
    const form2Ref = React.createRef();

    const [current, setCurrent] = useState(0); // 记录当前的步骤
    const [username, setUsername] = useState(""); // 记录用户名

    const next = async () => {
        await form1Ref.current.validateFields()
            .then(value => { // value是验证成功控件的值：{username:"张三",name:"郑十"}
                axios.post(`${HOST}:${PORT}/admins/test`, {user: value})
                    .then(result => {
                        if (result.data.status == 200) {
                            setUsername(value.username); // 记录当前的用户名，用来改密码
                            message.success(result.data.msg); // 输出后端传来的信息
                            setCurrent(current + 1);
                        } else {
                            message.error(result.data.msg);
                        }
                    }).catch(err => {
                    console.log(err);
                })

            })
    }
    const prev = () => {
        setCurrent(current - 1);
    }

    const steps = [ // 账号验证的步骤
        {
            title: "账号核实",
            content: function () { // 输入用户名和真实姓名
                return (
                    <Form ref={form1Ref}>
                        <Row>
                            <Col span={24}>
                                <Form.Item name={"username"}
                                           rules={[
                                               {
                                                   required: true,
                                                   message: "用户名不能为空"
                                               }
                                           ]}>
                                    <Input placeholder={"请输入用户名"}
                                           style={{borderRadius: "24px", width: "300px"}}
                                           prefix={<UserOutlined style={{color: 'rgba(0,0,0,0.25)'}}/>}
                                           size={"large"}/>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row>
                            <Col span={24}>
                                <Form.Item name={"name"}
                                           rules={[
                                               {
                                                   required: true,
                                                   message: "真实姓名不能为空"
                                               }
                                           ]}>
                                    <Input placeholder={"请输入真实姓名"}
                                           size={"large"}
                                           style={{borderRadius: "24px", width: "300px"}}
                                           prefix={<UserOutlined style={{color: 'rgba(0,0,0,0.25)'}}/>}/>
                                </Form.Item>
                            </Col>
                        </Row>
                    </Form>
                )
            }
        },
        {
            title: "修改密码",
            content: function () {
                return (
                    <Form ref={form2Ref}>
                        <Row>
                            <Col span={24}>
                                <Form.Item name="password"
                                           rules={[
                                               {
                                                   required: true,
                                                   message: '密码不能为空'
                                               }
                                           ]}>
                                    <Input.Password
                                        style={{borderRadius: "24px", width: "300px"}}
                                        prefix={<LockOutlined style={{color: "rgba(0,0,0,0.25)"}}/>}
                                        placeholder={"请输入密码"}
                                        size={"large"}/>
                                </Form.Item>
                            </Col>
                        </Row>
                        <Row>
                            <Col span={24}>
                                <Form.Item name="password"
                                           rules={[
                                               {
                                                   required: true,
                                                   message: '密码不能为空'
                                               }

                                           ]}>
                                    <Input.Password
                                        style={{borderRadius: "24px", width: "300px"}}
                                        prefix={<LockOutlined style={{color: "rgba(0,0,0,0.25)"}}/>}
                                        placeholder={"请输入密码"}
                                        size={"large"}/>
                                </Form.Item>
                            </Col>
                        </Row>
                    </Form>
                )
            }
        }
    ]

    return (
        <div className={"loginPage"}>
            <section className={"find-content"}>
                <div className={"back"}>
                    <NavLink to={"/"} style={{color: "#91A0B5"}}><LeftOutlined/>返回</NavLink>
                </div>
                <h2 style={{padding: "10px 30px"}}>忘记密码</h2>
                <Steps current={current} style={{width: "300px", marginBottom: "30px"}}>
                    {steps.map(item => (
                        <Step key={item.title} title={item.title}></Step>
                    ))}
                </Steps>
                <div className={"steps-content"}>{steps[current].content()}</div>
                <div>
                    {current == 0 && (
                        <Button type={"primary"} style={{width: "300px", borderRadius: "24px"}} size={"large"}
                                onClick={() => next()}>下一步</Button>
                    )}
                    {current == 1 && (
                        <Button type={"primary"} style={{width: "300px", borderRadius: "24px"}}
                                size={"large"}>修改密码</Button>
                    )}
                </div>
            </section>
        </div>
    )
}

export default Findpwd;
