import React from 'react';
import {Input, Button, Form, Row, Col, Avatar} from "antd";
import {UserOutlined, LockOutlined} from "@ant-design/icons";
import {HOST, PORT} from "../config/apiconfig";
import "../css/login.css";
import axios from "axios";
import {BrowserRouter} from "react-router-dom";
import logo from '../logo.png';

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: " ",
            password: " ",
        }
    }

    login = () => {
    }

    render() {
        return (
            <BrowserRouter>
                <section className="login-content">
                    <div>
                        <img src={logo} style={{width:"200px"}} alt="logo"/>
                    </div>
                    <div>
                        <h2>装修小程序后台管理系统</h2>
                        <hr/>
                        <br/>
                        <Form>
                            <Row>
                                <Col span={24}>
                                    <Form.Item
                                        name={"username"}
                                        rules={[  //规则
                                            {
                                                required: true,
                                                message: '用户名不能为空'
                                            }
                                        ]}
                                    >
                                        <Input
                                            prefix={<UserOutlined style={{color: 'rgba(0,0,0,0.25)'}}/>}
                                            placeholder={' 请输入用户名'}/>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row>
                                <Col span={24}>
                                    <Form.Item name='userPwd'
                                               rules={[
                                                   {
                                                       required: true,
                                                       message: '密码不能为空'
                                                   }

                                               ]}
                                    >
                                        <Input.Password
                                            prefix={<LockOutlined style={{color: "rgba(0,0,0,0.25)"}}/>}
                                            placeholder={'请输入密码'}
                                        />

                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row>
                                <Col span={24}>
                                    <Button type={"primary"} className={'login-form-button'}>登录</Button>
                                </Col>
                            </Row>
                            <Row>
                                <Col style={{float: "right", backgroundColor: "red"}}>忘记密码？</Col>
                            </Row>
                        </Form>
                    </div>
                </section>
            </BrowserRouter>
        )
    }

}

export default Login;
