import React, {useEffect, useState} from "react";
import {BrowserRouter} from "react-router-dom";
import {Button, Form, Input, message, Radio, Select, Space, Table, Tag} from "antd";
import axios from "axios";
import {HOST, PORT} from "../config/apiconfig";
import {CheckOutlined, CloseOutlined, DeleteOutlined, EditOutlined} from "@ant-design/icons";
import {Option} from "antd/es/mentions";


export const Administrator = () => {
    const [admins, setAdmins] = useState([]); // 存放所有的管理员数据
    const [state, setState] = useState(false); // 记录当前页面是否修改
    const [editing, setEditing] = useState(false); // 设置初始不可编辑

    const addButton = [<Button type={"primary"}>添加</Button>] // 控制添加按钮显示

    // 查询所有的管理员
    const allAdministrator = () => {
        axios.get(`${HOST}:${PORT}/admins/all`)
            .then(result => {
                if (result.data.status === 200) {
                    setAdmins(result.data.data);
                }
                if (result.data.status == 400) {
                    message.error(result.data.msg);
                }
            })
    }

    // 定义管理员身份标签
    const tagArr = [
        <Tag color={"red"}>超级管理员</Tag>,
        <Tag color={"green"}>普通管理员</Tag>,
        <Tag color={"default"}>账号已停用</Tag>
    ]

    const columns = [ // 定义表格的列
        {
            title: "编号",
            dataIndex: "id",
            key: "id"
        },
        {
            title: "用户名",
            dataIndex: "username",
            key: "username",
            editable: true
        },
        {
            title: "姓名",
            dataIndex: "name",
            key: "name",
            editable: true
        },
        {
            title: "身份",
            dataIndex: "role",
            key: "role",
            render: role => (
                <div key={"role"}>
                    {tagArr[role]}
                </div>
            ),
            editable: true
        },
        {
            title: "上次登录",
            dataIndex: "last_login_at",
            key: "last_login_at"
        },
        {
            title: "创建时间",
            dataIndex: "created_at",
            key: "created_at"
        },
        {
            title: "更新时间",
            dataIndex: "updated_at",
            key: "updated_at"
        },
        {
            title: "操作",
            key: "action",
            render: (text, record, index) => {// text：当前行的值，record：当前行的数据，index：当前行索引
                // text和record是一样的结果，显示表格的具体信息,index是索引，该索引是表格的索引0,1,2
                return editing ? ( // 是否可以编辑
                    <Space size={"middle"}>
                        <Button type={"link"} icon={<CheckOutlined/>}>修改</Button>
                        <Button type={"link"} danger icon={<CloseOutlined/>} onClick={() => {
                            setEditing(false);
                        }}>取消</Button>
                    </Space>
                ) : (
                    <Space size={"middle"}>
                        <Button type={"link"} icon={<EditOutlined/>} onClick={() => {
                            setEditing(true);
                        }}>编辑</Button>
                        <Button type={"link"} icon={<DeleteOutlined/>} danger>删除</Button>
                    </Space>
                );
            }
        }
    ];

    const EditableCell = ({editing, dataIndex, title, inputType, record, index,children, ...restProps}) => {
        if (dataIndex === "role") {
            return <td {...restProps}>
                {editing ? (
                    <Form.Item name={"role"}>
                        <Select defaultValue={0}>
                            <Option value={0}>超级管理员</Option>
                            <Option value={1}>普通管理员</Option>
                            <Option value={2}>账号已停用</Option>
                        </Select>
                    </Form.Item>
                ):(
                    children
                )}
            </td>
        }
    }

    const modifyColumns = columns.map(item => { // 定义修改的表格
        if (!item.editable) { // 如果该格子不可以修改，则不改
            return item;
        }
        return {
            ...item,
            onCell: (record, rowIndex) => ({
                record,
                dataIndex: record.dataIndex,
                title: record.title,
                editing: editing,
                inputType: "text"
            })
        }
    })

    useEffect(() => allAdministrator(), [state]); // 设置刷新当前页面的条件

    return (
        <BrowserRouter>
            <div style={{padding: "30px 20px"}}>
                <div style={{width: "40%"}}>
                    <Input.Group compact style={{display: "flex"}}>
                        <Select defaultValue={"文章标题"}>
                            <Select.Option value={"title"}>文章标题</Select.Option>
                            <Select.Option value={"cate"}>文章类别</Select.Option>
                        </Select>
                        <Input style={{width: "200px"}}/>&nbsp;&nbsp;&nbsp;
                        <Button type={"primary"}>搜索</Button>&nbsp;&nbsp;&nbsp;
                        {addButton[sessionStorage.getItem("role")]}
                    </Input.Group>
                </div>
                <Table rowKey={record => record.id} columns={columns} dataSource={admins}
                       style={{marginTop: "20px"}}></Table>
                <Radio.Group/>
            </div>
        </BrowserRouter>
    )
}
