import React, {useState} from "react";
import "../css/login.css";
import {Button, Col, Form, Input, Row, Steps} from "antd";
import {LeftOutlined, LockOutlined, UserOutlined} from "@ant-design/icons";
import {NavLink} from "react-router-dom";

const {Step} = Steps;

const Findpwd = () => {
    const steps = [ // 账号验证的步骤
        {
            title: "账号核实",
            content: function () { // 输入用户名和真实姓名
                return (
                    <Form>
                        <Row>
                            <Col span={24}>
                                <Form.Item name={"username"}
                                           rules={[
                                               {
                                                   required: true,
                                                   message: "用户名不能为空"
                                               }
                                           ]}>
                                    <Input placeholder={"请输入用户名"}
                                           style={{borderRadius: "24px"}}
                                           prefix={<UserOutlined style={{color: 'rgba(0,0,0,0.25)'}}/>}
                                           size={"large"}/>
                                </Form.Item>
                            </Col>
                        </Row>
                    </Form>
                )
            }
        },
        {
            title: "修改密码",
            content: "第二部分"
        }
    ]
    const [current, setCurrent] = useState(0);
    const next = () => {
        setCurrent(current + 1);
    }
    const prev = () => {
        setCurrent(current - 1);
    }
    return (
        <div className={"loginPage"}>
            <section className={"find-content"}>
                <div className={"back"}>
                    <NavLink to={"/"} style={{color: "#91A0B5"}}><LeftOutlined/>返回</NavLink>
                </div>
                <h2 style={{padding: "25px 30px"}}>忘记密码</h2>
                <Steps current={current}>
                    {steps.map(item => (
                        <Step key={item.title} title={item.title}></Step>
                    ))}
                </Steps>
                <div className={"steps-content"}>{steps[current].content()}</div>
                <div className={"steps-action"}>
                    {current < steps.length - 1 && (
                        <Button type={"primary"} onClick={() => next()}>下一步</Button>
                    )}
                    {current == steps.length - 1 && (
                        <Button type={"primary"}>修改密码</Button>
                    )}
                </div>
                <Form>
                    <Row>
                        <Col span={24}>
                            <Form.Item
                                name={"username"}
                                rules={[  // 规则
                                    {
                                        required: true,
                                        message: '用户名不能为空'
                                    }
                                ]}
                            >
                                <Input
                                    style={{borderRadius: "24px"}}
                                    prefix={<UserOutlined style={{color: 'rgba(0,0,0,0.25)'}}/>}
                                    placeholder={"请输入用户名"}
                                    size={"large"}/>
                            </Form.Item>
                        </Col>
                    </Row>
                    <Row>
                        <Col span={24}>
                            <Form.Item
                                name={"username"}
                                rules={[  // 规则
                                    {
                                        required: true,
                                        message: '用户名不能为空'
                                    }
                                ]}
                            >
                                <Input
                                    style={{borderRadius: "24px"}}
                                    prefix={<UserOutlined style={{color: 'rgba(0,0,0,0.25)'}}/>}
                                    placeholder={"请输入用户名"}
                                    size={"large"}/>
                            </Form.Item>
                        </Col>
                    </Row>
                    <Row>
                        <Col span={24}>
                            <Form.Item name="password"
                                       rules={[
                                           {
                                               required: true,
                                               message: '密码不能为空'
                                           }

                                       ]}>
                                <Input.Password
                                    style={{borderRadius: "24px"}}
                                    prefix={<LockOutlined style={{color: "rgba(0,0,0,0.25)"}}/>}
                                    placeholder={"请输入密码"}
                                    size={"large"}/>
                            </Form.Item>
                        </Col>
                    </Row>
                    <Row>
                        <Col span={24}>
                            <Form.Item name="password"
                                       rules={[
                                           {
                                               required: true,
                                               message: '密码不能为空'
                                           }

                                       ]}>
                                <Input.Password
                                    style={{borderRadius: "24px"}}
                                    prefix={<LockOutlined style={{color: "rgba(0,0,0,0.25)"}}/>}
                                    placeholder={"请输入密码"}
                                    size={"large"}/>
                            </Form.Item>
                        </Col>
                    </Row>
                    <Row>
                        <Col span={24}>
                            <Button type={"primary"} style={{width: "100%", borderRadius: "24px"}}
                                    size={"large"}>确定</Button>
                        </Col>
                    </Row>
                </Form>
            </section>
        </div>
    )
}

