import React from "react";
import {BrowserRouter} from "react-router-dom";
import {Button, Input, Modal, Table} from "antd";
import axios from "axios";
import {HOST, PORT} from "../config/apiconfig";
import Draggable from "react-draggable";

class Cate extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            allcate: [], // 存放所有的类别
            addVisiable: false, // 是否显示添加模态框
            bounds: {left: 0, top: 0, bottom: 0, right: 0}, // 拖拽模态框初始参数
            disabled: true, // 是否可以拖拽
        }
    }

    getAllCate = () => { // 获取所有的案例
        axios.get(`${HOST}:${PORT}/cate/all`)
            .then(result => {
                if (result.data.status === 200) {
                    this.setState({
                        allcase: result.data.data
                    })
                    console.log(this.state.allcate)
                }
            }).catch(err => {
            console.log(err);
        })
    }

    addCateModal = () => { // 打开添加类别的模态框
        this.setState({
            addVisiable: true
        })
    }

    handleCancel = () => { // 取消模态框
        this.setState({
            addVisiable: false
        })
    }

    onStart = (event, uiData) => {
        const {clientWidth, clientHeight} = window.document.documentElement;
        
    }

    componentDidMount() {
        this.getAllCate(); // 页面加载完成后获取全部案例数据
    }

    render() {
        return (
            <BrowserRouter>
                <div style={{padding: "30px 20px"}}>
                    <Input.Group compact>
                        <Input id={"searchCate"} style={{width: "200px"}} placeholder={"请输入案例名称"}/>&nbsp;&nbsp;&nbsp;
                        <Button type={"primary"}>搜索</Button>&nbsp;&nbsp;&nbsp;
                        <Button type={"primary"} onClick={this.addCateModal}>添加</Button>
                    </Input.Group>
                    <Table style={{marginTop: "20px"}} dataSource={this.state.allcate} rowKey={record => record.id}>
                        <Table.Column title={"类别编号"} key={"id"} dataIndex={"id"}></Table.Column>
                        <Table.Column title={"类别名称"} key={"name"} dataIndex={"name"}></Table.Column>
                        <Table.Column title={"类别标识"} key={"img"} dataIndex={"img"}></Table.Column>
                        <Table.Column title={"创建时间"} key={"created_at"} dataIndex={"created_at"}></Table.Column>
                        <Table.Column title={"更新时间"} key={"updated_at"} dataIndex={"updated_at"}></Table.Column>
                        <Table.Column title={"操作"} key={"actions"} dataIndex={"actions"}></Table.Column>
                    </Table>
                    <Modal title={<div style={{width: "100%", cursor: "move"}} // 当鼠标移动到标题上时，鼠标变成十字
                                       onMouseOver={() => { // 当鼠标移动进标题时，设置为可以拖拽
                                           if (this.state.disabled) {
                                               this.setState({
                                                   disabled: false // 将不可拖拽关掉
                                               })
                                           }
                                       }}
                                       onMouseOut={() => { // 当鼠标移动出标题时，设置不可拖拽
                                           this.setState({
                                               disabled: true // 打开不可拖拽
                                           })
                                       }}>添加类别</div>}
                           visible={this.state.addVisiable}
                           centered={true}
                           okText={"提交"}
                           cancelText={"取消"}
                           onCancel={this.handleCancel}
                           modalRender={modal => (
                               <Draggable disabled={this.state.disabled} bounds={this.state.bounds}
                                          onStart={(event, uiData)}>

                               </Draggable>
                           )}
                    ></Modal>
                </div>
            </BrowserRouter>
        )
    }
}

export default Cate;
